UserCurrent:
query UserCurrent {
  user_current {
    id
    username
    name: username
    email
    is_superuser
    library {
      pk
    }
    read_later {
      pk
    }
    post_votes {
      id
      is_vote_positive
      post {
        id
        type
      }
    }
    post_tag_votes {
      id
      is_vote_positive
      is_changed_my_mind
      post {
        id
      }
      tag {
        id
      }
    }
    connection_groups {
      id
      name
      connections {
        id
        username
        name: username
      }
    }
  }
}

HighlighterCreate:
mutation HighlighterCreate($id: ID!, $text: String!, $text_prefix: String, $text_postfix: String) {
  post_highlight_create(
    data: {post: {set: $id}, text: $text, text_postfix: $text_postfix, text_prefix: $text_prefix}
  )
}

GetPostHighlights:
query GetPostHighlights($ids: [ID!]!) {
  post_highlights(post_ids: $ids) {
    id
    text
    text_prefix
    text_postfix
    created_at
    post {
      ...CommentFieldsFragment
      parent_root {
        ...PostFragment
      }
    }
  }
}

fragment PostFragment on PostTypeI {
  id
  __typename
  type
  category
  title
  content_polite
  content_direct
  content_rant
  source
  source_author
  post_source {
    id
    id_external
    created_at_external
  }
  image {
    url
    name
  }
  crunchbase_url
  github_url
  url
  domain
  comments_count
  author {
    id
    username
    avatar {
      url
    }
  }
  votes {
    id
    is_vote_positive
    author {
      id
    }
  }
  parent {
    id
    __typename
    title
    ... on PostToolType {
      tool_type
      content_polite
      content_direct
      content_rant
      domain
      github_url
      crunchbase_url
    }
    tags {
      ...PostTagFragment
    }
  }
  updated_at
  tags {
    ...PostTagFragment
  }
}

fragment PostTagFragment on PostTagType {
  id
  votes {
    id
    post {
      id
    }
    author {
      id
      username
    }
    is_vote_positive
  }
  name
  label
  description
  is_important
  is_review_tag
  tag_parent {
    id
    name
  }
  author {
    id
    username
  }
  tag_children {
    id
  }
}

fragment CommentFieldsFragment on PostTypeI {
  id
  type
  __typename
  author {
    id
    username
    avatar {
      url
    }
  }
  parent {
    id
    type
  }
  parent_root {
    id
  }
  content_polite
  content_direct
  content_rant
  created_at
  updated_at
  source_author
  post_source {
    id
    id_external
    created_at_external
    json
  }
  votes {
    id
    is_vote_positive
    author {
      id
    }
  }
}

SearchToolsForSelection:
query SearchToolsForSelection($title: String) {
  post_tools(filters: {title: {i_contains: $title}}) {
    id
    title
    tool_type
    content_polite
    content_direct
    content_rant
    github_url
    crunchbase_url
    domain
    source
    source_author
    tags {
      id
      name
      label
    }
  }
}
