UserCurrent:
query UserCurrent {
  user_current {
    id
    username
    name: username
    email
    is_superuser
    library {
      pk
    }
    read_later {
      pk
    }
    post_votes {
      id
      is_vote_positive
      post {
        id
        type
      }
    }
    post_tag_votes {
      id
      is_vote_positive
      is_changed_my_mind
      post {
        id
      }
      tag {
        id
      }
    }
    connection_groups {
      id
      name
      connections {
        id
        username
        name: username
      }
    }
  }
}

PostAuthor:
query PostAuthor($id: ID!) {
  post_generic(pk: $id) {
    id
