}

PostVoteUpdateOrCreateOrUpdate:
mutation PostVoteUpdateOrCreateOrUpdate($id: ID!, $isVotePositive: Boolean) {
  post_vote_update_or_create(id: $id, is_vote_positive: $isVotePositive)
}

ToolTagsQuery:
query ToolTagsQuery($name: String, $is_review_tag: Boolean!) {
  tags(
    filters: {is_review_tag: {exact: $is_review_tag}, name: {i_contains: $name}, OR: {is_review_tag: {exact: $is_review_tag}, description: {i_contains: $name}}}
  ) {
    id
    name
    label
    is_review_tag
    tag_parent {
      id
      name
    }
  }
}

